INSERT INTO parks (name, description, latitude, longitude, address, amenities, is_fenced, has_water, has_shade)
VALUES
  ('Dog Park at Capital Springs Recreation Area', 'Bring Fido to blow off some steam at the dog park at Capital Springs Recreation Area in Madison, WI.  This fenced off-leash pup recreation space features separate sections for small and large dogs, trees and trails. An annual park permit or a small daily fee is required.', 43.0268428, -89.3485262, '3398 Lake Farm Rd, Madison, WI 53711', ARRAY['fenced', 'separate_areas', 'shade_trees', 'walking_trails']::text[], true, false, true),
  ('Granville Dog Park', 'Granville Dog Park in Milwaukee, WI is a fee-based park. With the purchase of a day pass or annual membership, Fido can explore this expansive park which features grassy hills, wooded areas and narrow walking trails along the Menomonee River. Unlike many off-leash areas, Granville Dog Park does not have wide open spaces for Fido to run around in clear sight of his owner.', 43.1506249, -88.0580315, '11745 Fond du Lac Ave, Milwaukee, WI 53224', ARRAY['shade_trees', 'walking_trails']::text[], false, false, true),
  ('Kaukauna Dog Park', 'The 13-acre Kaukauna Dog Park, located in Kaukauna, WI, contains three separate grassy areas. There is a fenced-in small dog area, a very large fenced-in area for all dogs and a fenced-in pond where dogs can swim. You can also check out the walking trail around the perimeter of the pond, which includes two bridges built by local Boy Scouts. The park is well-lit after dark and there are soda machines available for thirsty dog owners.', 44.3027764, -88.2437066, '366 Farmland Ct, Kaukauna, WI 54130', ARRAY['fenced']::text[], true, true, false),
  ('Dog Park at Prairie Moraine County Park', 'Bring Fido to the dog park at Prairie Moraine County Park in Verona, WI. This 79-acre off-leash pup recreation area is known for being the best in the surrounding area, as it features tons of trails, grassy meadows with big hills, prairie grass, beautiful wildflowers and open space for your pup to run and explore. There are disposal bags, benches and picnic tables available for humans. A permit is required.', 42.9624095, -89.5179244, '6679 Wesner Ln, Verona, WI 53593', ARRAY['benches', 'picnic_tables', 'walking_trails']::text[], false, false, false),
  ('White River Dog Park', 'Bring Fido to expend his energy with friends at White River Dog Park in Lake Geneva, WI. The park is located behind Eastview Elementary School and opposite Lake Geneva Skatepark. The dog park is fully fenced and features water fountains for both humans and dogs, a bone shaped bench, shaded areas and pet waste stations.', 42.5946199, -88.4300187, '600 Waverly St, Lake Geneva, WI 53147', ARRAY['benches', 'fenced', 'shade_trees', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Room to Roam Dog Park of Rome', 'Fido is invited to play at Room To Roam Dog Park of Rome in Nekoosa, WI. Dogs of all sizes are welcome to play in this 9.8-acre space. Amenities include a walking path, benches, water and waste stations, and agility equipment.', 44.1987464, -89.8308057, '1351 Apache Ave, Nekoosa, WI 54457', ARRAY['agility_equipment', 'benches', 'walking_trails', 'waste_bags', 'water_access']::text[], false, true, false),
  ('Minooka Dog Park', 'Bring Fido to Minooka Dog Park, a large 19-acre off-leash park in Waukesha, WI. This fenced park has two separate enclosed areas for large and small breeds. There is no water on site, so owners should bring water and a bowl for Fido. Please note that this park can become muddy after periods of wet weather.', 42.9788701, -88.1956261, '1927 E Sunset Dr, Waukesha, WI 53189', ARRAY['fenced', 'water_access']::text[], true, false, false),
  ('The Boneyard', 'The Boneyard in Madison, WI, is a dog park with the joys of a biergarten, featuring on-and-off-leash recreation for dogs and humans to enjoy together. While you hang out, the Boneyard''s "Barktenders" will pull you a pint from 16 taps that include beer, coffee, wine and more. A day pass or annual membership is required. Dog owners must sign an electronic waiver on the website before bringing Fido to the beer garden.', 43.112771, -89.310317, '1018 Walsh Rd, Madison, WI 53714', ARRAY[]::text[], false, false, false),
  ('Tails & Trails Off-Leash Dog Park', 'Dogs can enjoy off-leash play with paid admission at the Tails & Trails Off-Leash Dog Park in Waupaca, WI. The park offers walking trails and a large swimming pond. Annual memberships and day passes are available.', 44.3307488, -89.1514477, 'E1595 County Rd Q, Waupaca, WI 54981', ARRAY['walking_trails']::text[], false, true, false),
  ('The Hounds & Tap', 'With a daily or monthly dog membership, dogs can play year-round in an off-leash, 2,500 sq ft. indoor or 5,500 sq ft outdoor dog park while you relax with friends, drink with your furry friend, watch the game, relax and meet other dog lovers. There are separate large and small dog areas for playing. Pups can get their sniff on in the exclusive K9Grass area or romp on the play equipment. Doggie pools available during the summer. Beer, wine, fun cocktails, water and soda are available for the humans.', 43.1197442, -88.1318117, 'W175N5645 Technology Dr, Menomonee Falls, WI 53051', ARRAY['water_access']::text[], false, true, false),
  ('Shiloh Road Bark Park', 'Bring Fido to the Shiloh Road Bark Park in Sturgeon Bay, WI, which features separate areas for large and small dogs and benches for humans.', 44.8061814, -87.3772019, '1660 Shiloh Rd, Sturgeon Bay, WI 54235', ARRAY['benches', 'separate_areas']::text[], false, false, false),
  ('Chippewa Falls HappyTails Dog Park', 'Fido is invited to play at Chippewa Falls HappyTails Dog Park in Wisconsin. The off-leash facility has over 53 acres with three different sections for dogs of all sizes. Amenities include a watering hole, benches with shade and pathways surrounding the area. The park is maintained and run by volunteers who operate on donations.', 44.9369054, -91.3929348, '841 Chippewa Xing Blvd, Chippewa Falls, WI 54729', ARRAY['benches', 'shade_trees', 'walking_trails', 'water_access']::text[], false, true, true),
  ('McFarland Dog Park', 'Bring Fido to McFarland Dog Park, an off-leash fenced space in McFarland, WI. The park features an enclosed shelter, a heated, indoor washing station, a seasonal outdoor washing station, restrooms, and plenty of shade from mature trees. Your dog must be licensed, and a permit is required, which to use the park.', 43.004977, -89.2733753, '6300 Elvehjem Rd, McFarland, WI 53558', ARRAY['fenced', 'restroom', 'shade_trees']::text[], true, false, true),
  ('Brown County Dog Park', 'Brown County Dog Park is a great place for Fido run and play off leash in Hobart, WI. It''s fully fenced with and grass covered with dirt patches, wooded areas, and water access. The park features separate spaces for large and small dogs, as well as one combined. Amenities include trails, a creek, agility obstacles, handicap access, benches, water fountains, waste stations, and restrooms nearby. Park passes are required. Dog owners may walk their dogs on-leash through the rest of the park.', 44.5244922, -88.1343313, '1000 Pleasant Ln, Hobart, WI 54301', ARRAY['agility_equipment', 'benches', 'fenced', 'restroom', 'separate_areas', 'shade_trees', 'walking_trails', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Shepard Dog Park', 'Dogs love playing off-leash at Shepard Dog Park in Rhinelander, WI. The fenced-in area is great for bringing your pup to socialize and exercise. Two separate sections for small and large dogs include waste and water stations, toy boxes and benches. There is also a walking path within the park that is shaded by large evergreen trees.', 45.6301204, -89.4242659, '808-844 Boyce Dr, Rhinelander, WI 54501', ARRAY['benches', 'fenced', 'separate_areas', 'shade_trees', 'walking_trails', 'water_access']::text[], true, true, true),
  ('Three Lakes Dog Park', 'Three Lakes Dog Park in Three Lakes, WI, is 2 acres is size and has separate areas for small and large dogs. Dogs must be vaccinated to use the park. Visitors will find running water during the warmer months, restrooms, a dog wash area, a pavilion, and dog waste stations.', 45.7983817, -89.1789118, '1921 Epler Ct, Three Lakes, WI 54562', ARRAY['restroom', 'separate_areas', 'waste_bags', 'water_access']::text[], false, true, false),
  ('Auggie Land', 'Bring Fido to blow off some steam at the Auggie Land dog park in Chetek, WI. This pup recreation space features separate sections for small and large dogs, plenty of space to run, agility equipment, a pavilion and a dog wash.', 45.332615, -91.6558916, '871 24 1/2 St, Chetek, WI 54728', ARRAY['agility_equipment', 'separate_areas']::text[], false, false, false),
  ('Terri Tinsley Dog Exercise Area', 'Fido is invited to play at Terri Tinsley Dog Exercise Area, off Highway 26, in Johnson Creek, WI. The park is 109 acres and has two large and small dog areas. A daily permit or annual tag is required to entire the off-leash facility. Please refer to their website on how to become a member.', 43.065815, -88.777857, 'W5098 Canine Dr, Johnson Creek, WI 53038', ARRAY[]::text[], false, true, false),
  ('Viking County Park Dog Park', 'Your pet will go wild for the fenced-in and off-leash dog park at Viking County Park in Stoughton, WI, which includes a dock and beach where your pet can go for a dip, as well as paths and picnic tables.', 42.9334288, -89.2025772, '1582 County Hwy N, Stoughton, WI 53589', ARRAY['fenced', 'picnic_tables', 'walking_trails']::text[], true, false, false),
  ('Northland Dog Park', 'Fido is invited to play at Northland Dog Park in Appleton, WI. This fenced off-leash facility provides three separate sections, including a wooded 12 acres with walking trails and a small and large dog area. Waste stations are offered on-site, but it''s encouraged to bring your own along with water for your pup.', 44.28783, -88.351493, '2830 French Rd, Appleton, WI 54911', ARRAY['fenced', 'separate_areas', 'shade_trees', 'walking_trails', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Platteville Community Dog Park', 'Nestled against the Rountree Branch stream in Platteville, WI, the five-acre Platteville Community Dog Park provides a safe and spacious area for dogs to frolic and play. It''s a fenced-in off-leash area, with separate sections for small and large dogs, a wooded walking path, poop disposal stations, picnic tables, parking and a porta-potty.', 42.7323588, -90.4648979, '946 Valley Rd, Platteville, WI 53818', ARRAY['fenced', 'parking', 'picnic_tables', 'restroom', 'separate_areas', 'shade_trees', 'walking_trails']::text[], true, false, true),
  ('Waupaca County Dog Park', 'Shade trees, benches, picnic tables, and agility equipment are amenities of the Waupaca County Dog Park. This two-acre fenced-in recreation space is a local favorite among the Waupaca, WI, community.', 44.3455096, -89.0965865, '601 Lakeside Pkwy, Waupaca, WI 54981', ARRAY['agility_equipment', 'benches', 'fenced', 'picnic_tables', 'shade_trees']::text[], true, false, true),
  ('Dog Park at Yahara Heights County Park', 'Dogs can play off leash in the 20-acre dog park at Yahara Heights County Park in Waunakee, WI, which features ample grassy space and water for Fido to dip his paws into. Please note dog permits are required.', 43.1546004, -89.40536, '5488 Catfish Ct, Waunakee, WI 53597', ARRAY['water_access']::text[], false, true, false),
  ('Tails n Trails Dog Park', 'Fido is invited to play at the Tails n Trails Dog Park in Milton, WI. This off-leash facility has two separate areas for small and large dogs. Amenities include water, a walking trail and off-street parking.', 42.7735835, -88.9666275, '862 W High St, Milton, WI 53563', ARRAY['parking', 'separate_areas', 'walking_trails', 'water_access']::text[], false, true, true),
  ('Burlington Dog Park', 'Fido is invited to play at Burlington Dog Park in Wisconsin. Dogs of all sizes are welcome to join for socialization. Amenities include chairs along with a waste and water station.', 42.669813, -88.2671306, '480 S Calumet St, Burlington, WI 53105', ARRAY['water_access']::text[], false, true, false),
  ('Merrill Dog Park at the MARC', 'Fido is invited to play at the Merrill Dog Park at the MARC in Wisconsin. The off-leash area is 2.5 acres and features a separate section for small dogs. If the park is busy, they have pathways connecting to Council Grounds State Park.', 45.1915025, -89.7259475, '1100 Marc Dr, Merrill, WI 54452', ARRAY['separate_areas', 'walking_trails']::text[], false, false, false),
  ('Laura & Peter Mossakowski Family Dog Park', 'Fido will love playing at the Laura & Peter Mossakowski Family Dog Park in Bellevue, WI. This 6.5-acre recreation space is a place where furry friends have a place to run, roll around in the mud, meet new four-legged friends and even catch a ball or a Frisbee. Note that permits are required to use this dog park.', 44.460631, -88.011728, '2282 Bellevue St, Bellevue, WI 54311', ARRAY[]::text[], false, false, false),
  ('Paws & Play Dog Park', 'Paws & Play Dog Park is a 4.5-acre fenced-in area in Marshfield, WI. The park is relied on donations and has two separate areas for small and large dogs. Amenities include waste stations, picnic tables and water during the warmer months.', 44.6493446, -90.1722062, 'E 21st St & S Peach Ave, Marshfield, WI 54449', ARRAY['fenced', 'picnic_tables', 'separate_areas', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Off-Leash Dog Area at Mead Park', 'Fido is invited to play at the off-leash dog area at Mead Park in Stevens Point, WI. The beach opens 30 minutes before sunrise, and your furry friend can play until 11:00 PM daily. Owners must be within the fenced-in area with a leash readily available while watching their pup. Park users without an animal may also use this beach.', 44.5217524, -89.5931195, '1201 W Whitney St, Stevens Point, WI 54481', ARRAY['fenced']::text[], true, false, false),
  ('Hudson Dog Park', 'Bring Fido to blow off some steam at the Hudson Dog Park in Hudson, WI. This two-acre pup recreation area is fully fenced and has waste bags on site. All dogs must be licensed in the municipality where the owner and pet reside.', 44.9789803, -92.7217686, '908 Carmichael Rd, Hudson, WI 54016', ARRAY['fenced', 'waste_bags']::text[], true, false, false),
  ('The City of Superior Dog Park', 'Fido is invited to play at The City of Superior Dog Park near the Millennium Trailhead in Wisconsin. This off-leash area has a two-way entrance and various trees spread throughout for shade. Amenities also include benches and waste stations.', 46.7050166, -92.1236832, 'N 28th St, Superior, WI 54880', ARRAY['benches', 'shade_trees', 'waste_bags']::text[], false, false, true),
  ('Bark & Brew', 'Bark & Brew is a pet-friendly spot in Howard, WI. The first of its kind, this off-leash, indoor/outdoor, climate-controlled establishment has a dog menu and delicious beverages for humans. Though they do not sell human food, you can bring in your own or have it delivered from your favorite takeout spot!', 44.5644788, -88.0799234, '2514 Glendale Ave, Howard, WI 54313', ARRAY[]::text[], false, false, false),
  ('Sister Bay Dog Park', 'If Fido needs a place to stretch his legs, bring him for a romp at the Sister Bay Dog Park in Sister Bay, WI. Entirely fenced in and equipped with picnic tables and mutt mitts, the park is open year-round, from dawn until dusk, and is free to the public.', 45.18901, -87.1101437, '2124 Autumn Ct, Sister Bay, WI 54234', ARRAY['fenced', 'picnic_tables', 'waste_bags']::text[], true, false, false),
  ('River Falls Dog Park', 'Bring your pup to socialize and get some exercise at the River Falls Dog Park in River Falls, WI. This 16-acre fenced off-leash pup recreation area features separate sections for small and large dogs, picnic tables, benches and dog waste stations. No running water is available, so make sure to bring some for Fido! There is plenty of parking on site.', 44.8323622, -92.6482193, 'W10225 County Rd FF, River Falls, WI 54022', ARRAY['benches', 'fenced', 'parking', 'picnic_tables', 'separate_areas', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Point Dog Park', 'Fido is invited to play at Point Dog Park in Stevens Point, WI. The off-leash facility contains two separate sections that are approximately 30 acres. Amenities include trails, wooded areas, benches and waste stations.', 44.5096973, -89.5810604, '601 Mason St, Stevens Point, WI 54481', ARRAY['benches', 'separate_areas', 'shade_trees', 'walking_trails', 'waste_bags']::text[], false, false, true),
  ('Spring Green Dog Park', 'Bring Fido to blow off some steam at the Spring Green Dog Park in Spring Green, WI. This off-leash pup recreation area features separate sections for small and large dogs, and is a great place to bring your pooch to play, have fun and make some new friends. Dog owners are reminded to keep an eye on their pup as they play and to clean up any mess that they leave behind.', 43.1738239, -90.081069, 'S12947 Shifflet Rd, Spring Green, WI 53588', ARRAY['separate_areas']::text[], false, false, false),
  ('Florence Bark Park', 'Bring your pup to the Florence Bark Park, a one-acre fenced-in off-leash dog park in Florence, WI. There are benches for humans and plenty of space for your pup to run, socialize and get some exercise.', 45.9224395, -88.2639785, '5628 Forestry Dr, Florence, WI 54121', ARRAY['benches', 'fenced']::text[], true, false, false),
  ('Webster Community Dog Park', 'Fido is invited to play at the Webster Community Dog Park in Wisconsin. This off-leash facility has two separate sections for small and large dogs and a training area. Amenities include waste stations and double-gated entrances for safety.', 45.8676535, -92.3647619, '27383 Hwy 35 N, Webster, WI 54893', ARRAY['separate_areas', 'waste_bags']::text[], false, false, false),
  ('Viroqua Bark Park', 'Fido is invited to play at Viroqua Bark Park in Wisconsin. The off-leash facility is separated into two sections for large and small dogs. Amenities include solar lights, picnic tables and benches. Please note there is no water source in this park, so pack some for your pup.', 43.5717494, -90.8797659, '700 Power Dr, Viroqua, WI 54665', ARRAY['benches', 'lighting', 'picnic_tables', 'separate_areas', 'water_access']::text[], false, false, false),
  ('Marvin & Marie Schweer''s Dog Park', 'Marvin & Marie Schweer''s Dog Park is an off-leash recreation space in DeForest, WI. The park ensures pups play safely and comfortably with two separate fenced-in areas for large and small dogs. The park is free for residents, but visitors may pay a one-time or annual fee to let Fido play.', 43.2600591, -89.3381002, '700 Stevenson St, De Forest, WI 53532', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('Dog Exercise Area at Standing Rocks Park', 'If a member, Fido is welcome to play at the dog exercise area at Standing Rocks Park in Stevens Point, WI. Your pup will go wild for the chance to run and play off-leash on the 32 fenced-in acres. Please refer to their website on how to become a member.', 44.4309716, -89.402045, '7695 Standing Rocks Rd, Stevens Point, WI 54481', ARRAY['fenced']::text[], true, false, false),
  ('Bark & Brew', 'The pet-friendly Bark & Brew in Green Bay, WI, is the state''s first dog bar, featuring off-leash indoor fun and a spacious fenced outdoor area for pups. Humans 21 and up can enjoy over 70 craft beers, cocktails, wine, and more, while Dog Butlers keep tails wagging with playtime, fresh water, and treats from the Bone Bar.', 44.5646097, -88.0799983, '2514 Glendale Ave, Green Bay, WI 54313', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('West Bend Dog Park', 'If a member, Fido is invited to play at West Bend Dog Park in Wisconsin. Your leashed furry friend will love exploring 10 acres that is completely fenced in. Amenities include ample parking, waste stations, hiking trails, benches and trees. Please note you must pack your own water.', 43.3898599, -88.2008558, '1150 S 18th Ave, West Bend, WI 53095', ARRAY['benches', 'fenced', 'parking', 'shade_trees', 'walking_trails', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Estabrook Dog Park', 'Estabrook Dog Park in Milwaukee, WI is a fee-based park. With purchase of a day pass or annual membership, Fido can explore Estabrook Dog Park and enjoy playing off leash with his friends. The park is situated within Estabrook Park and sits alongside the Oak Leaf Trail. This 3-acre park has a separate area for small dogs, and the ground is a mixture of grass and wood chips.', 43.0993909, -87.9052973, 'Estabrook Pkwy, Milwaukee, WI 53217', ARRAY['separate_areas']::text[], false, false, false),
  ('Nashotah Dog Park', 'Fido is invited to play at Nashotah Dog Park in Wisconsin. The off-leash section is separated for large and small breeds. Each area contains walking trails and watering stations for your pup.', 43.103051, -88.411467, 'W330 N5113 County Trunk Hwy C, Nashotah, WI 53058', ARRAY['walking_trails', 'water_access']::text[], false, true, false),
  ('Mukwonago Dog Exercise Park', 'Mukwonago Dog Exercise Park is a spacious 34-acre area in Mukwonago, WI, which is designated for off leash play. There are dog watering stations on the site, and three separately-fenced areas for large and small breeds, and a ''Lazy Bones Zone'' for senior, shy, disabled or low energy pets.', 42.862765, -88.3992254, 'W325 S9925 Beulah Rd, Mukwonago, WI 53149', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Bay View Dog Park', 'Bay View Dog Park is a fee-based park in Milwaukee, WI. With purchase of a day pass or annual membership, Fido can explore Bay View Dog Park and enjoy playing off leash with his friends. The park comprises of one fenced area for dogs of all sizes, and there is communal watering area on site.', 43.0037673, -87.8993264, 'E Lincoln Ave at S Bay St, Milwaukee, WI 53207', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Palmer Park Off-Leash Dog Area', 'Fido is welcome to play, exercise, and explore within the off-leash dog area at Palmer Park in Janesville, WI. From 5:00 AM - 11:00 PM year-round, this designated area welcomes pups to visit with their owners. Except in established dog exercise areas, pets are prohibited from Janesville parks and trails between May 15th and September 15th of each year. Note that this space is not fully fenced.', 42.6764174, -88.9950285, '2201 E Raccine St, Janesville, WI 53545', ARRAY['fenced', 'walking_trails']::text[], false, false, false),
  ('City of New Richmond Off Leash Dog Park', 'The City of New Richmond Off Leash Dog Park, located in New Richmond, WI, is an 11.5-acre fenced recreation spot with mowed trails and benches. Make sure to bring water!', 45.1297725, -92.5822906, '2202 185th Ave, New Richmond, WI 54017', ARRAY['benches', 'fenced', 'walking_trails', 'water_access']::text[], true, false, false),
  ('Katherine Kearney Carpenter Park', 'Fido is invited to play at the pet-friendly Katherine Kearney Carpenter Park in Mequon, WI. Your pup is welcome to explore off-leash around the various trails. The park, in total, is 35 acres.', 43.1993869, -87.917675, '801 W Zedler Ln, Mequon, WI 53092', ARRAY['walking_trails']::text[], false, false, false),
  ('Zander Dog Park', 'Bring Fido out for a day of play at Zander Dog Park in Two Rivers, WI! The area is fully fenced and grass covered with shady trees, picnic tables, benches, and waste stations. Don''t forget water for your pup.', 44.1464979, -87.5758851, '1423 River Pl, Two Rivers, WI 54241', ARRAY['benches', 'fenced', 'picnic_tables', 'shade_trees', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Otter Creek Off-Leash Dog Park', 'If a member, Fido is invited to play at the Otter Creek Off-Leash Dog Park in Eau Claire, WI. The large off-leash area welcomes dogs of all sizes to play. Amenities include benches, trees for shade and water available for dogs from May until October. Please refer to their website for acquiring a membership or daily permit.', 44.7697537, -91.4223883, '4503 House Rd, Eau Claire, WI 54701', ARRAY['benches', 'shade_trees', 'water_access']::text[], false, true, true),
  ('Northwoods Humane Society Bark Park', 'The Northwoods Humane Society in Hayward, WI, features 87 beautiful acres that include two miles of walking trails, an outdoor pavilion for meetings, picnics and gatherings, and the Bark Park.  The Bark Park consists of two outdoor dog parks, one park for large dogs and a separate park for small dogs.  Both are open to the community.', 46.0238785, -91.4025017, '10812 N O''Brien Hill Rd, Hayward, WI 54843', ARRAY['separate_areas', 'walking_trails']::text[], false, false, false),
  ('Paws & Play Dog Park', 'Dogs are welcome to run and play off leash in the grassy, fenced Paws & Play Dog Park in Manitowoc, WI. From this location, visitors and their pups can also enjoy beach access or take a path out to Manitowoc North Breakwater Lighthouse which is close by.', 44.097265, -87.6537394, '495 Maritime Dr, Manitowoc, WI 54220', ARRAY['fenced', 'lighting', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Karen A. Nelson Memorial Dog Park', 'Karen A. Nelson Memorial Dog Park is located off Highway 38, just north of Racine, WI. Allow Fido to play at this fenced 4-acre off-leash park. The park has two separate areas for large and small dogs, picnic tables, and pet waste stations. There is no water on site so bringing water and a bowl for your dog is recommended.', 42.7493887, -87.823622, '3800 Northwestern Ave, Racine, WI 53404', ARRAY['fenced', 'picnic_tables', 'separate_areas', 'waste_bags', 'water_access']::text[], true, false, false),
  ('Love''s Travel Stop', 'Love''s Travel Stop in Elkhorn, WI, is a dog-friendly gas station and rest area. Across America, you''ll find Love''s Travel Stops that offer facilities for traveling canines. This location has a fully-fenced dog park with a convenient waste station.', 42.6525679, -88.5442137, '100 Commerce Ct, Elkhorn, WI 53121', ARRAY['fenced', 'waste_bags']::text[], true, false, false),
  ('Lakeland Area Dog Park', 'Fido is invited to play at Lakeland Area Dog Park in Minocqua, WI. This off-leash facility has two separate areas for small and large dogs, totaling 1.65 acres. Amenities include benches, trees for shade and a water fountain. Donations are appreciated to help maintain the park.', 45.8878092, -89.7237968, '9863 Plaza Dr, Minocqua, WI 54548', ARRAY['benches', 'separate_areas', 'shade_trees', 'water_access']::text[], false, true, true),
