INSERT INTO parks (name, description, latitude, longitude, address, amenities, is_fenced, has_water, has_shade)
VALUES
  ('Wag ''N Paddle', 'Wag ''N Paddle is an indoor climate-controlled park and heated swimming pool designed for dogs in Naperville, IL. This pet-friendly park is perfect for pooches who love to paddle and play. The 9,000-square-feet park is monitored by staff to make sure dogs and their owners are on their best behavior. In addition to providing a pool and park to play in, they also offer a number of swimming classes in the pool for senior dogs and training sessions with qualified dog trainers for all levels of pooch in the park too. Fido can gain unlimited access to the indoor dog park for a membership fee, and for for an extra fee can enjoy private swimming sessions. There are a number of other pricing options available on their website for regular and one-time users.', 41.769836, -88.1997751, '1847 W Jefferson Ave, Naperville, IL 60540', ARRAY[]::text[], false, false, false),
  ('Batavia Bark Park', 'Batavia Bark Park in Batavia, IL, offers three acres of fully fenced space where dogs can run and play off-leash. The park features a pavilion, shady trees and a small beach area for extra fun. While no permit is required and admission is free, dogs must be leashed when entering and exiting the park.', 41.845284, -88.396675, '40W101 W Main St, Batavia, IL 60510', ARRAY['fenced', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Lakewood Dog Park', 'Lakewood Dog Park in Wauconda, IL, is a 66-acre fenced space where Fido can run and play off leash. Amenities include open meadows, mowed trails, parking, toilets, a water pump, and a shade shelter. A permit is required and can be purchased online. The park is closed until 11 AM on the first and third Thursdays from April to October.', 42.2625904, -88.1016657, '27511 N Fairfield Rd, Wauconda, IL 60084', ARRAY['fenced', 'parking', 'walking_trails', 'water_access']::text[], true, true, true),
  ('Hammel Woods Dog Park', 'Hammel Woods Dog Park in Shorewood, IL, provides an exercise area where dogs can run free. A shelter and benches provide comfort for the canines'' human companions. A permit is required for use of the Dog Park. Other amenities at Hammel Woods include multi-purpose trails, campsites, picnic shelters, and access to the DuPage River for canoeing/kayaking and fishing.', 41.5318923, -88.1959501, '550 Brook Forest Ave, Shorewood, IL 60404', ARRAY['benches', 'walking_trails', 'water_access']::text[], false, true, false),
  ('Maxwell Dog Park', 'Fido can play off-leash at Maxwell Dog Park in Normal, IL. This fenced dog park has a separate small dog area, water fountains of all sizes (seasonally available), benches and trees. Please remember to leash your pup outside of the dog park and clean up any mess after them. After heavy rains and snow melt, the dog park may be closed.', 40.517408, -89.0470558, '1850 Gregory St, Normal, IL 61761', ARRAY['benches', 'fenced', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Oakwood Dog Park', 'Pups can play off-leash at Oakwood Dog Park in Morton, IL. This 3-acre fenced dog park offers a separate area for small dogs, shade from mature trees, a human and canine water fountain (available seasonally) and a tire play area. You and Fido can find the dog park at the back of pet-friendly Oakwood Park. Please leash your pup outside of the dog park and remember to clean up after them.', 40.638406, -89.4504393, '22216 N Main St, Morton, IL 61550', ARRAY['fenced', 'separate_areas', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Evergreen Bark Park', 'Evergreen Bark Park in Effingham, IL is located north of Evergreen Hollow Park and east of Workman Sports and Wellness Complex. Fido can play off leash in either the large dog area or adjoining small dog space, both of which are fully fenced. Each area has a double-gated entry allowing owners to unleash their pets prior to entering the space. Additional amenities include a dog water fountain, benches, picnic tables and waste stations. Once funding permits, there will also be a pavilion, shade structures, and a dog washing area to rinse off muddy pets before you return home.', 39.1385536, -88.5498169, '1208 N Maple St, Effingham, IL 62401', ARRAY['benches', 'double_gated', 'fenced', 'picnic_tables', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Jackson Bark', 'Jackson Bark is a dog park located in Jackson Park, just south of the Museum of Science and Industry in Chicago, IL. The fenced park features agility equipment and toys for Fido to enjoy.', 41.7863353, -87.5784488, '6000 S Lake Shore Dr, Chicago, IL 60637', ARRAY['agility_equipment', 'fenced']::text[], true, false, false),
  ('Tomahawk Dog Park', 'Converted from an old baseball diamond, is pet-friendly Tomahawk Dog Park in Collinsville, IL. You can bring your pup down to enjoy some off-leash time in this grassy area located in a corner of Woodland Park.', 38.6870295, -89.9660526, '1002 California Ave, Collinsville, IL 62234', ARRAY[]::text[], false, false, false),
  ('Wiggley Field', 'Bring Fido out for a day of play at Wiggley Field in Peru, IL. Inspired by Chicago''s Wrigley Field, you''ll find tis designated dog area in the southwest corner of Veterans Park. It''s fully fenced and grass covered with double-gated entry, agility obstacles, benches, and waste stations. There''s a water fountain and restroom in the main park area.', 41.3434748, -89.1373472, '2600 Plank Rd, Peru, IL 61354', ARRAY['agility_equipment', 'benches', 'double_gated', 'fenced', 'restroom', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Hawk Hollow Preserve', 'Hawk Hollow Preserve in Bartlett, IL, offers 34 acres of off-leash area for dogs to enjoy along with a three-mile multipurpose trail connecting to trails in Mallard Lake and West Branch forest preserves. A permit is required for off-leash use. Dogs must be leashed in all other areas of the pet-friendly preserve. The area is open during regular preserve hours.', 41.9679192, -88.1682748, '1099 Bittersweet Dr, Bartlett, IL 60103', ARRAY['walking_trails', 'water_access']::text[], false, true, false),
  ('Messenger Marsh Dog Park', 'The Forest Preserve District of Will County''s 5-acre fenced Messenger Marsh Dog Park provides an exercise area where dogs can run free in Homer Glen, IL. The dog park also features a smaller 1-acre park for dogs under 30 pounds. A shelter and benches provide comfort for human companions. A permit is required for use of the dog park. Other amenities at Messenger Marsh include a picnic shelter and multi-use trails suitable for equestrians. Located on South Bell Road, about 0.25-mile South of 159th Street.', 41.5941142, -87.9250497, 'S Bell Rd, Homer Glen, IL 60491', ARRAY['benches', 'fenced', 'shade_trees', 'walking_trails']::text[], true, false, true),
  ('Community Bark West', 'Community Bark West at Community Park West in Glenview, IL, is a 6-acre off-leash park with electronic key entry, benches, shade, and a separate small-dog area. Park passes are available to residents and nonresidents on a first-come, first-served basis until the 1,000-dog maximum is reached. Dogs are welcome in the surrounding park area, as long as they are leashed. The dog park is closed for maintenance on Tuesdays and Thursdays from 8:30 AM to 11:30 AM.', 42.0702917, -87.8627392, '1001 Zenith Dr, Glenview, IL 60025', ARRAY['benches']::text[], false, false, true),
  ('Fred Anderson Dog Park', 'Architecturally structured dog park named for Chicago Jazz musician and owner of the Velvet Lounge, the Fred Anderson Dog Park is part of the Chicago Park District in Chicago, IL. Located in the South Loop, this park features separate areas for small and large dogs, as well as spouting fountains in the summer and a performance area.', 41.8596272, -87.6256128, '1615 S Wabash Ave, Chicago, IL 60616', ARRAY['separate_areas']::text[], false, false, false),
  ('Maple Park Dog Park', 'Maple Park Dog Park in Oak Park, IL, is a dedicated off-leash site that covers approximately 12,000 square feet. The park features a crushed granite surface, benches, canine water fountain and an electronic entry system. The dog park is open daily to permitted users.', 41.8678591, -87.8038429, '1105 S Maple Ave, Oak Park, IL 60130', ARRAY['benches', 'water_access']::text[], false, true, false),
  ('Miller Meadow Off-Leash Dog Area', 'The Forest Preserves of Cook County currently operates Miller Meadow Off-Leash Dog Area in Forest Park, IL, for visitors to let their dogs enjoy the pet-friendly preserves without a leash. Please note that these are the only locations within the Forest Preserves where off-leash dogs are allowed. Dog owners wishing to enter the Off-Leash Dog Areas must first purchase an annual membership. The annual membership includes one membership card and one key to enter any of the Forest Preserves’ Off-Leash Dog Areas.', 41.8632127, -87.8334841, '2175 S 1st Ave, Forest Park, IL 60130', ARRAY[]::text[], false, false, false),
  ('Bo''s Run', 'Bo''s Run in Hoffman Estates, IL, is a fully fenced off-leash dog park that includes water, agility equipment, benches and a keypad entry. There are 2-acres of field for dogs to enjoy with 5-foot tall fences. The park also features separate areas for small and large dogs. Memberships are required for entry and can be purchased online.', 42.0890452, -88.1049102, '3600 Lexington Dr, Hoffman Estates, IL 60192', ARRAY['agility_equipment', 'benches', 'fenced', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Central Bark Dog Park', 'Central Bark Dog Park in Lemont, IL, is a small, fenced dog park where Fido can enjoy some off-leash time. The park features separate areas for small and large dogs, as well as benches for your convenience. A permit is required for entry and can be acquired at the Township Office.', 41.6574531, -88.0163391, '16300 Alba, Lemont, IL 60439', ARRAY['benches', 'fenced', 'separate_areas']::text[], true, false, false),
  ('Norwood Dog Friendly Area', 'The Norwood Dog Friendly Area in Chicago, IL, is a spacious off-leash area located within Norwood Park. The park features a doggy water fountain and is located just north of the service yard on Avondale Ave.', 41.9874936, -87.7935378, '5850 N Avondale Ave, Chicago, IL 60631', ARRAY['water_access']::text[], false, true, false),
  ('Duck Farm Dog Park', 'Pets will love to play off-leash at Duck Farm Dog Park, a fenced dog park in Lake Villa, IL. The 48-acre park features wetlands, fields and grass trails. Toilets and drinking fountains are available for your convenience. A permit is required and can be purchased online.', 42.414553, -88.047155, '1439 E Grand Ave, Lake Villa, IL 60046', ARRAY['fenced', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Puptown at MargatePark', 'Puptown at Margate Park in Chicago, IL, is an off-leash area that attracts a loyal local crowd. You can bring Fido with you to enjoy the area and socialize with other pups who are enjoying the fenced-in section. Make sure you clean up any mess that your pooch leaves behind.', 41.9718526, -87.6512512, '4900 N Marine Dr, Chicago, IL 60640', ARRAY['fenced']::text[], true, false, false),
  ('Oldfield Oaks Dog Park', 'Oldfield Oaks Dog Park in Darien, IL, is pet friendly. It features two fenced areas, including a 6-acre area for large dogs with and a small area, too. Wander the turf looped trail or limestone trail and play a game of fetch with the gathered locals. The park is open every day of the week from one hour after sunrise until one hour after sunset. Entry into the park is restricted to dogs with a permit. Permits can be purchased online. Dog owners are reminded to clean up any mess that their pups leave behind.', 41.7229766, -88.0071539, 'N Frontage Rd, Darien, IL 60561', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('Park No. 551 Dog Friendly Area', 'Park No. 551 Dog Friendly Area is a 1.19-acre green space in Chicago, IL. Bring your pet to play off-leash at this shady, fenced, neighborhood dog park.', 41.888349, -87.6442, '353 N DesPlaines St, Chicago, IL 60661', ARRAY['fenced']::text[], true, false, false),
  ('Happy Bark Dog Park', 'Fido can become a member of Happy Bark Dog Park and romp among other neighborhood pets in Evergreen Park, IL. The park features three fenced-in spaces: one for small dogs, one for large dogs and an obedience training area. Dogs can be registered at Village Hall.', 41.727044, -87.6891739, '91st and Rockwell, Evergreen Park, IL 60805', ARRAY['fenced', 'separate_areas', 'training_area']::text[], true, false, false),
  ('Bosly''s Backyard', 'Unleash a new adventure for you and Fido by visiting Bosly''s Backyard, a dog-friendly indoor play space in the Ravenswood neighborhood in Chicago. Bosly''s Backyard is a new concept in dog exercise designed to promote physical & cognitive development, and to strengthen the bond with your dog through a shared one-on-one experience. Private play sessions can be booked for a duration of 30, 45, or 60 minutes, allowing Fido uninterrupted off-leash exercise and enrichment in 2400 square feet of space. Bosly''s Backyard has a variety of exciting and engaging agility equipment and toys for Fido to utilize during your session. You are also welcome to bring Fido''s favorite toys or treats from home. Secure a buddy pass for Fido''s best friend so they too can play in a safe environment. Start planning Fido''s next birthday party at Bosly''s Backyard, or schedule a time to celebrate your dog''s gotcha day or other special occasion!', 41.9547671, -87.6757973, '4011 N Ravenswood Ave # 108, Chicago, IL 60613', ARRAY['agility_equipment']::text[], false, false, false),
  ('Brent Leh Dog Park', 'Fido can play off-leash at Brent Leh Dog Park, located just one block behind the fire station in Edwardsville, IL. This fenced dog park has astroturf, agility equipment and seasonally available canine water stations.', 38.807364, -89.9559018, '328 Olive St, Edwardsville, IL 62025', ARRAY['agility_equipment', 'fenced', 'water_access']::text[], true, true, false),
  ('Gregory Island Dog Park', 'Gregory Island Dog Park, nestled along Indian Trail just west of Route 25, offers ample space for both small dogs (35 pounds and under) and larger breeds (35 pounds and over) to stretch their legs and play. Situated amidst the picturesque backdrop of the Fox River on either side, the park ensures a scenic environment for furry friends to enjoy. It''s worth mentioning that while larger breeds have access to the water from their side, this section lacks a perimeter fence.', 41.7814543, -88.3130409, '100-140 E Indian Trail Rd, North Aurora, IL 60542', ARRAY['separate_areas', 'water_access']::text[], false, true, false),
  ('Friends Forever Humane Society Dog Park', 'Friends Forever Humane Society Dog Park is a 7-acre park in Freeport, IL. It''s divided into a small and big dog area. There''s also an agility park where your pooch can stay busy and learn some new tricks. The park is fenced so your dog can run, play, socialize off-leash, and go for a swim.', 42.2483516, -89.6288749, '966 W Rudy Rd, Freeport, IL 61032', ARRAY['agility_equipment', 'fenced']::text[], true, false, false),
  ('Washington Bark District Off-Leash Dog Park', 'Bring Fido to play off-leash at Washington Bark District Off-Leash Dog Park in Washington, IL. Located on the west side of pet-friendly Meadow Valley Park near Ernest Street, this fenced dog park has separate areas for small and large dogs and is quickly accessible from the parking lot. Waste bags are provided, so cleaning up after your pup is convenient.', 40.6909829, -89.4642986, '601 Ernest St, Washington, IL 61571', ARRAY['fenced', 'parking', 'separate_areas', 'shade_trees', 'waste_bags']::text[], true, false, true),
  ('Mascoutah Dog Park', 'Fido can play off-leash at Mascoutah Dog Park located within the pet-friendly Scheve Park in Mascoutah, IL. The fenced dog park has lots of space for pups to stretch their legs, and separate sections for small and large dogs. The centerpiece is an adirondack chair placed among some nice shady trees. Pups and their humans should enter the park via the North 6th Street entrance and can find the dog park north of the "Big Ditch" and behind the Berger-Kiel Log House.', 38.4986059, -89.8130944, '851 N 6th Street, Mascoutah, IL 62258', ARRAY['fenced', 'separate_areas', 'shade_trees']::text[], true, false, true),
  ('Katz Dog Park and Off-Leash Natural Area', 'Katz Dog Park and Off-Leash Natural Area in DeKalb, IL offers separate fenced areas for large and small dogs as well as roughly nine acres of natural space for Fido and friends to enjoy off leash. Take a walk on the trails with your pup and explore within the safety of the fenced perimeter. Park amenities include drinking fountains, waste receptacles and shaded benches.', 41.952347, -88.75428, '393 W Dresser Rd, DeKalb, IL 60115', ARRAY['benches', 'fenced', 'separate_areas', 'walking_trails', 'water_access']::text[], true, true, true),
  ('Jacksonville PetSafe Bark Park', 'Bring Fido for some off-leash fun at Jacksonville PetSafe Bark Park in Jacksonville, IL. This fenced dog park offers a separate section for small pups, climbing structures, shade structures, benches and waste stations. A canine and human water fountain is available during the warmer months of the year.', 39.7085545, -90.212199, '894 E Vandalia Rd, Jacksonville, IL 62650', ARRAY['benches', 'fenced', 'separate_areas', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Hair of the Dog', 'Hair of the Dog is a pet-friendly bar in Centralia, IL, that features a supervised, off-leash dog park. Memberships are required for dogs. Fido must be current on all required vaccinations and be properly socialized for group play. Daily, monthly and annual memberships are available. Humans without dogs are welcome without a membership. Drinks are available at the bar, and guests are welcome to bring their own food to enjoy while Fido plays.', 38.5244118, -89.1334547, '325 S Poplar St, Centralia, IL 62801', ARRAY[]::text[], false, false, false),
  ('Park 468', 'Park 468 is a grassy dog park in the West Elsdon community area of Chicago, IL, near Chiago Midway Airport. The park is fully fenced but visitors should ensure both the north and south gates are closed before letting Fido off leash. The park doesn''t have any seating and owners should bring their own water.', 41.7918394, -87.7374166, '4556 W 56th St, Chicago, IL 60629', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Bark Park', 'Dogs can play off-leash at this dog park in Vandalia, IL. Pets should be supervised at all times. The park closes at dusk daily.', 38.8242278, -88.8295233, '118 E Madison St, Vandalia, IL', ARRAY[]::text[], false, false, false),
  ('Numosh Park', 'Furry friends of Tiskilwa, IL, love visiting the local dog park, Numosh Park. This neighborhood recreation area is located on an old football field, making it a great spot for pups to run, explore, and socialize with their pet companions. To ensure safety and comfort, the fully-fenced space features two separate sections for small and large dogs.', 41.2959984, -89.5135335, '340 Jefferson St, Tiskilwa, IL 61368', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('June Clark Cheney Dog Park', 'June Clark Cheney Dog Park (JCC Park) is an off-leash recreation area in south Pana, IL. Bring Fido out for a day of exercise, play and social time with other pups! The space is fully fenced, covered in grass, and has logs for dogs to climb on. There is also a separate space for small dogs. Additional amenities include waste clean-up stations, water access, and lots of picnic table seating for you to watch your pup!', 39.3731762, -89.082085, '183 US-51, Pana, IL 62557', ARRAY['fenced', 'picnic_tables', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Harvard Dog Park', 'Harvard Dog Park in Harvard, IL welcomes Fido to enjoy some off-leash exercise and socializing with other canines. This large fenced area offers lots of space for your pup to run as well as a shade shelter, seating, a waste station and a dog water fountain.', 42.4212788, -88.6365218, '300 Lawrence Rd, Harvard, IL 60033', ARRAY['fenced', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Tag-A-Long Dog Park', 'Tag-A-Long Dog Park, located on 10 acres in Morris, IL, is a fenced park featuring separate areas for small and large dogs to enjoy some off-leash exercise. Amenities include shade, benches and a covered shelter with one picnic table. There''s also a dog wash station and lights to continue playing into the evening.', 41.3728573, -88.4318452, '605 Michael Dr, Morris, IL 60450', ARRAY['benches', 'fenced', 'lighting', 'picnic_tables', 'separate_areas']::text[], true, false, true),
  ('Veterans Dog Park', 'Fido has been invited to play at Veterans Dog Park in Minooka, IL. This off-leash area welcomes both large and small dogs to roam freely. The park features statues that honor the United States Armed Forces, as well as beautifully landscaped areas. Visitors can enjoy grassy spaces, a pavilion and a concrete walking loop inside.', 41.4545692, -88.2612137, 'S Wabena Ave &, E Wapella St, Minooka, IL 60447', ARRAY['separate_areas']::text[], false, false, false),
  ('Prairie Wolf Dog Park', 'Prairie Wolf Dog Park in Lake Forest, IL is a 44-acre pet-friendly area which is ideal for off-leash play. The spacious fully-fenced park has different terrain and the surrounding area features walking trails and a pond for pups to enjoy during their visit. A Lake Forest County dog park permit is required to access Prairie Wolf Dog Park. This area can become muddy after heavy rainfall.', 42.2071155, -87.8626938, '2075 S Waukegan Rd, Lake Forest, IL 60045', ARRAY['fenced', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Whalon Lake Dog Park', 'The Forest Preserve District of Will County''s 8-acre fenced Whalon Lake Dog Park provides an exercise area where dogs can run free. Located in Naperville, IL, the dog park features a smaller 2.5-acre area for dogs under 30 pounds, and a shelter and benches provide comfort for the canines'' human companions. A permit is required for use. Other amenities at Whalon Lake include multi-purpose trails, picnic shelters, and access to an 80-acre lake for boating and fishing.', 41.7176509, -88.102716, '1480 Royce Rd, Naperville, IL 60565', ARRAY['benches', 'fenced', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Wiggly Field', 'Wiggly Field in Chicago, IL, is a public dog park that boasts a fenced-in area, doggie water fountains, waste bags and a double gate to prevent escapes. Wiggly Field is located within Noethling Playlot Park, so there''s plenty of places to explore nearby too.', 41.9305329, -87.653434, '2645 N Sheffield Ave, Chicago, IL 60614', ARRAY['fenced', 'waste_bags', 'water_access']::text[], true, true, false),
  ('McBark Dog Park', 'McBark Dog Park in McHenry, IL, is a 4-acre fenced space for Fido to have some off-leash fun. A  key card must be purchased in advance from City of McHenry for entry.', 42.3624192, -88.267355, '2500 N Richmond Rd, McHenry, IL 60051', ARRAY['fenced']::text[], true, false, false),
  ('Hound Town Dog Park', 'Dogs may romp and play off-leash at Hound Town Dog Park, a 3 acre dog park in Crystal Lake, ID. The park is fenced and located within the Lippold Park Sport Complex. A key card is required for entry and can be purchased at the Administration Office.', 42.2465138, -88.3555635, '851 IL-176, Crystal Lake, IL 60014', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Lincoln Dog Park', 'Lincoln Dog Park is a pet-friendly park in Aurora, IL. The park is fully fenced and equipped with shady trees, doggy water fountains, benches for humans, and plenty of space for a game of fetch. A permit is required to use the park.', 41.7555811, -88.3385168, '259 S Russell Ave, Aurora, IL 60506', ARRAY['benches', 'fenced', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Bradley Dog Park', 'Pups can romp and socialize off-leash at Bradley Dog Park in Peoria, IL. Located towards the back of pet-friendly Bradley Park, near the Peoria Park District building, this fenced, 5-acre dog park offers seasonally available water fountains and convenient waste stations. If Fido still has some energy left, you can clip on his leash and explore the trails and scenery, including a Japanese Bridge, within the larger park. This dog park is also located right off of I-74 so it makes for a nice rest stop for Fido.', 40.709872, -89.6407695, '1314 N Park Rd, Peoria, IL 61604', ARRAY['fenced', 'walking_trails', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Happy Tails Dog Park', 'Happy Tails Dog Park in Buffalo Grove, IL, is open to residents only. It has a large, fenced-in field and there is a small field for small dogs under 35 lbs. The park is located at the south end of the parking lot at the Sidney Mathias Metra Train Station.', 42.169265, -87.943417, '835 Commerce Ct, Buffalo Grove, IL 60089', ARRAY['fenced', 'parking', 'separate_areas']::text[], true, false, false),
  ('O''Fallon Dog Park', 'O''Fallon Dog Park is a 1.5-acre fenced dog park located within the pet-friendly Rock Springs Park in O''Fallon, IL. Inside the enclosed grassy area are shaded benches and picnic tables, climbing structures and pet waste stations. Maintenance is generally conducted on Friday mornings from 8:00 AM until 12:00 PM, and visitors may not be able to access the park during that time.', 38.5875862, -89.8877927, '1428 E 3rd St, O''Fallon, IL 62269', ARRAY['benches', 'fenced', 'picnic_tables', 'waste_bags']::text[], true, false, true),
  ('Bark Park', 'Bark Park in Lake in the Hills, IL, is a fenced dog park where Fido can exercise and socialize. Bring your pet to play off-leash at this park with shade and drinking water. Permits are required for entry and available through The Village.', 42.1985939, -88.3983048, '9027 Haligus Rd, Lake in the Hills, IL 60156', ARRAY['fenced', 'water_access']::text[], true, true, true),
  ('Stuart Sports Complex Dog Park', 'Fido can run and play at Stuart Sports Complex Dog Park in Aurora, IL. This 15-acre dog park features separate small and large dog areas, wetland and an agility course for your pup to enjoy.', 41.7317331, -88.3971405, '39W546 Baseline Rd, Sugar Grove, IL 60554', ARRAY['agility_equipment']::text[], false, false, false),
  ('James O. Breen Community Dog Park', 'James O. Breen Community Dog Park is a large 2.5 acre pet-friendly open space for your dog to enjoy when visiting St. Charles, IL. Allow Fido to get plenty of off-leash exercise and social play in a secure, safe and fully enclosed area. Open from 9am until dusk, the dog park has a water fountain, double-gated security entrance, dog-friendly equipment and activity stations, and an area specifically for small dogs. A parking area is close by and the park has covered seating for owners to sit and relax while Fido is at play.', 41.9100376, -88.3709791, 'Campton Hills Road and Peck Rd, St. Charles, IL 60175', ARRAY['double_gated', 'parking', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Stuart Park Dog Park', 'Pups can play off-leash at Stuart Park Dog Park, located within pet-friendly Stuart Park in Springfield, IL. This fenced dog park offers separate small and large dog sections, shade structures, sturdy plastic pools, seasonally available water sources, benches and waste stations.', 39.824117, -89.7023785, '1800 Winch Ln, Springfield, IL 62702', ARRAY['benches', 'fenced', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Rover Run Dog Park', 'Fido can play off-leash at Rover Run Dog Park, located within pet-friendly Schoonmaker Park in Machesney Park, IL. This 2.5-acre fenced dog park offers a separate section for small pups, agility equipment, seating areas, plenty of mature trees that provide shade, seasonally available water and more. Please make sure Fido is on-leash outside of the dog park.', 40.6331249, -89.3985283, '10893 Ventura Blvd, Machesney Park, IL 61115', ARRAY['agility_equipment', 'fenced', 'separate_areas', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Greenvalley Dog Park', 'Fido can play off-leash at Greenvalley Dog Park in Moline, IL. This 2-acre fenced space offers an open area for dogs to play and water is seasonally available from May through September (exact dates vary depending on the weather).', 41.46392, -90.454246, '6009 50th Ave, Moline, IL 61265', ARRAY['fenced', 'water_access']::text[], true, true, false),
