INSERT INTO parks (name, description, latitude, longitude, address, amenities, is_fenced, has_water, has_shade)
VALUES
  ('Rice Creek Off-Leash Dog Area', 'The Rice Creek Off-Leash Dog Area in Shoreview, MN is a large, fully-fenced 13-acre dog park with two double-gated entrances and a gated swimming hole. Other features include paths, a prairie, sandy areas and trees. There are restrooms and a water fountain in the Lexington Ave parking lot.', 45.1175942, -93.1683467, '5959 Lexington Ave N, Shoreview, MN, 55126', ARRAY['double_gated', 'fenced', 'parking', 'restroom', 'shade_trees', 'walking_trails', 'water_access']::text[], true, true, true),
  ('Dakota Woods Dog Park', 'Bring Fido to the 16-acre Dakota Woods Dog Park in Rosemount, MN. Pets will find plenty of room to exercise in this massive, fenced dog park featuring lots of wooded trails and double gates for added security. Bring water, and please note that a daily or annual dog pass is required.', 44.7083097, -93.0546196, '16470 Blaine Ave, Rosemount, MN, 55068', ARRAY['fenced', 'shade_trees', 'walking_trails', 'water_access']::text[], true, true, true),
  ('Battle Creek Dog Park', 'Battle Creek Dog Park is a spacious fully-enclosed 35-acre park for Fido to enjoy in Maplewood, MN. This plentiful space is free to use and visitors do not need a permit. The park has miles of wooded trails & walking paths, plenty of shaded areas and two small ponds where your pet can swim. Allow your companion to swim, explore and play off leash in this beautiful open area. There is no water at this site (except for the ponds) so visitors should also bring a bottle of drinking water.', 44.9406123, -93.0043514, '2350 Upper Afton Rd, Maplewood, MN, 55119', ARRAY['shade_trees', 'walking_trails', 'water_access']::text[], true, false, true),
  ('Minneapolis Airport Off-Leash Dog Park', 'Just south of Highway 62, the Minneapolis Airport Off-Leash Dog Park features year-round fun for you and Fido in 80 acres of mixed open space and paths bordering a large swamp/wetland area in Minneapolis, MN. Not all areas are fenced so dogs should have good recall. No permit is required. The land is owned by the Metropolitan Airport Commission and borders the airport runways. Since the park is not managed by the city, no amenities exist here (including trash receptacles). Bring water and waste bags with you, and please take them when you leave.', 44.8930504, -93.2356867, '6040 28th Ave S, Minneapolis, MN, 55417', ARRAY['fenced', 'walking_trails', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Bloomington Off-Leash Recreation Area', 'The Bloomington Off-Leash Recreation Area in Bloomington, MN is a dog''s dream! This 25-acre dog park features a very large grassy area, hills, trees, separate fenced areas and even a pond for those pups that like to swim. A dog license is required to use the park.', 44.8026147, -93.3611217, 'Nesbitt Ave & W 111th St, Bloomington, MN, 55438', ARRAY['fenced', 'shade_trees']::text[], true, false, true),
  ('Albert Lea Community Dog Park at Brookside Park', 'The Albert Lea Community Dog Park is located south of Brookside Boathouse in Brookside Park,  The off-leash dog park is divided into small dog and large dog zones. Facilities/amenities include plenty of shade, water, and parking available. The park is adjacent to a walking trail near Fountain Lake.', 43.666081, -93.373634, '501 Richway Dr W, Albert Lea, MN, 56007', ARRAY['parking', 'water_access']::text[], false, true, true),
  ('River Road Dog Park', 'Bring Fido to blow off some steam at the River Road Dog Park in Rochester, MN. This park includes separate large, fenced dog runs, perfect for pups to socialize and roam off leash. Please remember to pick up after your pup.', 44.0877805, -92.4802593, '5701-5899 W River Rd NW, Rochester, MN, 55901', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Higgins Dog Park', 'Higgins Dog Park is a dog park in Fergus Falls, MN. Higgins Dog Park is an off-leash play area where owners can enjoy the park and the chance to socialize with other dog lovers. This park offers two play areas, where dogs are separated by personality types, rather than size. Owners must always carry a leash for their dogs, and spike collars are strictly prohibited; aggressive dogs and females in heat are prohibited from entering the park.', 46.2744366, -96.0618353, '715 E Channing Ave, Fergus Falls, MN, 56537', ARRAY[]::text[], false, false, false),
  ('Elm Creek Park Reserve Dog Off-Leash Area', 'Fido will find more than enough room to play at the Elm Creek Park Reserve Dog Off-Leash Area in Champlin, MN. This 29-acre, fenced dog park is in the middle of the gorgeous Elm Creek Reserve. The park features a separate area for small dogs and a fenced wetland pond. A daily or annual pass is required. Dogs must be leashed in all areas outside of the off-leash areas.', 45.1682468, -93.4236697, '11750 Zachary Ln N, Champlin, MN, 55316', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('Cleary Lake Dog Off-Leash Area', 'The Cleary Lake Dog Off-Leash Area in Prior Lake, MN is a 28-acre fenced off-leash dog park that is part of the Three Rivers Park District. Take your pet for a run in the lovely prairie setting, which includes a separately fenced pond for dog swimming. A permit from the park district administration is required.', 44.6779058, -93.3968059, '6246 190th St E, Prior Lake, MN, 55372', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Crow-Hassan Dog Off-Leash Area', 'The Crow-Hassan Dog Off-Leash Area in Hanover, MN is a 40-acre unfenced dog park at the Crow-Hassan Park Reserve. There is plenty of space for Fido to run around and blow off steam, but please make sure to stay near and clean up after him. A daily or annual pass is required.', 45.2072977, -93.6260871, '27100 Territorial Rd, Hanover, MN, 55374', ARRAY['fenced']::text[], true, false, false),
  ('Dog Park at Brookdale Park', 'Pets are welcome to run and play off leash at the dog park at Brookdale Park in Brooklyn Park, MN. This fully-fenced dog run features hills and open spaces. An entry fee is required.', 45.092767, -93.333828, '7650 June Ave N, Brooklyn Park, MN, 55443', ARRAY['fenced']::text[], true, false, false),
  ('Indoor Zoomies', 'Indoor Zoomies in Minnetonka, MN is a dog park exclusively for small dogs 30 pounds and under. The play area is fully fenced in and has ramps for your dog to play on. There are also chairs for you to sit at and watch your pup. Fido can join in on the fun once they are registered and upload vet records on the site. They offer a Daily Pass, a 5 Pass Pack, and a 10 Pass Pack.', 44.9513872, -93.4183939, '10924 Greenbrier Rd, Minnetonka, MN, 55305', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('Waggin'' Tails Dog Park', 'The Waggin'' Tails Dog Park in Benson, MN is an off-leash, fenced-in area that is about two acres in size. This pup recreation area features running fresh water, separate fenced-in areas for small and large dogs, nighttime lighting, seating for humans, a dog pond and community dog toys. The park is sponsored and maintained by the Humane Society of Swift County.', 45.3081686, -95.6137177, 'US-12 & 22nd St S, Benson, MN, 56215', ARRAY['fenced', 'lighting', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Andy''s Bark Park', 'Andy''s Bark Park is an off-leash dog park in Woodbury, MN. The park has a pet watering station, pet waste disposal stations, mowed walking trails, benches, a sheltered area with picnic tables, and a portable restroom. Be careful as the area is not fully fenced.', 44.881971, -92.872327, '11664 Dale Rd, Woodbury, MN, 55129', ARRAY['benches', 'fenced', 'picnic_tables', 'restroom', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Anytime Puppy Playground', 'Fido can run and play with his friends at Anytime Puppy Playground in Apple Valley, MN. This 24-hour, membership-based dog park is fully indoors and can be accessed anytime using your phone. Please remember to clean up after your pup and read the park rules online in advance.', 44.7369997, -93.2084031, '6950 146th St W #120, Apple Valley, MN, 55124', ARRAY[]::text[], false, false, false),
  ('Dog Park at Bald Eagle-Otter Lakes Regional Park', 'Bring your pet to play off leash at the 10-acre, fully-fenced dog park at Bald Eagle-Otter Lakes Regional Park in White Bear Lake, MN. They offer designated small dog hours from 6:00 PM-7:00 PM on Tuesdays and 10:00 AM-11:00 AM on Saturdays.', 45.110826, -93.0433736, '5750 Otter Lake Rd, White Bear Lake, MN, 55110', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Plymouth Dog Park', 'The Plymouth Dog Park located in Plymouth, MN is the largest in the city, featuring a drinking fountain, a picnic shelter, a portable restroom, trails and a fenced area for small dogs. The off-leash area is bordered by private property. Please respect the surrounding neighbors and keep your pets within the confines of the dog park.', 45.0601012, -93.5003371, '17005 Chankahda Trl, Plymouth, MN, 55446', ARRAY['fenced', 'restroom', 'separate_areas', 'walking_trails', 'water_access']::text[], true, true, false),
  ('High Bridge Dog Park', 'High Bridge Dog Park, located at the site of a former coal power plant in St. Paul, MN, is a fully-fenced, seven-acre dog park with plenty of space for your pup to romp off leash. The dog park entrance is on the westernmost end of the park, near the parking lot', 44.9363532, -93.108564, '59 Randolph Ave, St. Paul, MN, 55102', ARRAY['fenced', 'parking']::text[], true, false, false),
  ('Lake Sarah Dog Off-Leash Area', 'Pets will go wild for the Lake Sarah Dog Off-Leash Area in Rockford, MN. This 30-acre fenced dog park includes trails, grassy spaces and play areas for Fido to run around off leash.', 45.0690137, -93.7122167, '6670 W Lake Sarah Dr, Rockford, MN, 55373', ARRAY['fenced', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Lake Chase Park Dog Park', 'Bring your pet to play off-leash at the dog park at Lake Chase Park in Owatonna, MN, which includes a separate small dog area, walking paths, play pieces for dogs, lights, a fire hydrant, running water and shade.', 44.1031916, -93.2438163, '1915 State Ave, Owatonna, MN, 55060', ARRAY['lighting', 'walking_trails', 'water_access']::text[], false, true, true),
  ('Dog Park at Perry Park', 'Bring Fido to play at the dog park located within Perry Park in Arden Hills, MN. This dog park was converted from an old baseball field into a fenced, off-leash area for Fido to exercise, socialize and play fetch. There is a double gate security system to ensure all dogs stay safe entering and exiting. Make sure to leash Fido back up when returning to the larger Perry Park.', 45.052783, -93.180142, '3700 New Brighton Rd, Arden Hills, MN, 55112', ARRAY['fenced']::text[], true, false, false),
  ('Dog Park at Pinecone Central Park', 'Bring Fido to play and exercise at the dog park at Pinecone Central Park in Sartell, MN. This full-fenced area is tucked into the corner of the park, down a gravel trail, with signage at the entrance. Be sure to bring some water for Fido and have proof of their rabies vaccination.', 45.6366452, -94.2296508, '1105 Central Park Blvd, Sartell, MN, 56377', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Dog Park at Southbridge Community Park', 'Bring Fido to blow off some steam at the dog park at Southbridge Community Park in Shakopee, MN. This park features a 10-acre all-dog area, a half-acre small dog run, a water station and walking paths. The fee to use the park is included in the City of Shakopee dog license. Please make sure your dog is properly licensed and read all rules and regulations before visiting the park', 44.7800185, -93.4310072, '7101 Southbridge Pkwy, Shakopee, MN, 55379', ARRAY['walking_trails', 'water_access']::text[], false, true, false),
  ('Lake Minnewashta Off-Leash Dog Area', 'The Lake Minnewashta Off-Leash Dog Area in Excelsior, MN, is home to about .8 miles of walking trails, a fenced pond, a separate area for small dogs, two large open spaces for interactive play with other dogs, and a water fountain for humans and pets. Pups must be licensed and vaccinated. Please clean up after your pet.', 44.8828793, -93.6076174, '6730 Ches Mar Dr, Excelsior, MN, 55331', ARRAY['fenced', 'separate_areas', 'walking_trails', 'water_access']::text[], true, true, false),
  ('Watertown Dog Park', 'Bring Fido to the Watertown Dog Park, featuring four acres of rolling terrain, tree canopy and small wetlands in Watertown, MN. This park includes completely fenced small and large dog run areas with vestibule entrances, potable water, waste bags and seating areas. This attraction is located adjacent to the Luce Line Trail and Paul Avenue with easy access to downtown and the Crow River.', 44.9608429, -93.824449, '11695 County Rd 20, Watertown, MN, 55388', ARRAY['fenced', 'shade_trees', 'waste_bags', 'water_access']::text[], true, true, true),
  ('Monti Paws Dog Park', 'Monti Paws Dog Park, located inside Meadow Oak Park, is a dog-friendly recreation area in Monticello, MN. There are two entrances to the park, one on the east side and one on the west side, and both are double-gated to allow for unleashing your pets before entering the park. The small dog area is limited to dogs weighing less than 25 pounds and features a smaller open grassy play area, a bench and a shade tree. The designated space for large dogs welcomes pups of all sizes and features an open grassy play area, watering station, benches, picnic tables and mature shade trees on the east side.', 45.2865034, -93.7621589, '6138-6500 Old State Hwy 152, Monticello, MN, 55362', ARRAY['benches', 'double_gated', 'picnic_tables', 'separate_areas', 'shade_trees', 'water_access']::text[], false, true, true),
  ('Rivertown Dog Park', 'Rivertown Dog Park in Hastings, MN, offers three different areas for Fido to play. Parking and dog waste bags are available. All dogs must be licensed, vaccinated and be wearing up-to-date license tags at all times.', 44.7355406, -92.8405023, '851 10th St E, Hastings, MN, 55033', ARRAY['parking', 'waste_bags', 'water_access']::text[], false, true, false),
  ('Dog Park at Lions Park', 'Bring Fido to run around, socialize and blow off steam at the dog park at Lions Park in Willmar, MN. This 2.5-acre off-leash dog recreation area is completely fenced and includes large and small dog sections, pet waste bags and a trash container. A shelter area and water faucet (seasonal) are also located on site.', 45.1083862, -95.0732962, '1336 22nd St SW, Willmar, MN, 56201', ARRAY['fenced', 'waste_bags', 'water_access']::text[], true, true, false),
  ('Buster Park', 'Buster Park is a pet-friendly recreation area in Brainerd, MN. This fenced-in dog park is installed at the south end of the Kiwanis Park. The area offers 47,000 square feet for your dog to explore! There are picnic tables, and large trees create plenty of shade to keep you and Fido cool during warmer weather.', 46.3449317, -94.2088697, 'Jenny St and E River Rd, Brainerd, MN, 56401', ARRAY['fenced', 'picnic_tables', 'shade_trees']::text[], true, false, true),
  ('City of Hutchinson Dog Park', 'Bring Fido to the City of Hutchinson Dog Park to romp off-leash in Hutchinson, MN! The park features a small dog area, a large dog area, grass, benches, a water source, dog waste stations and more. Water is shut off during the winter season. Rules and driving directions can be found on the dog park''s official website.', 44.8911662, -94.3632841, '427 1st Ave SE, Hutchinson, MN, 55350', ARRAY['benches', 'waste_bags', 'water_access']::text[], false, true, false),
  ('Motley Pet Stop & Rec Area', 'Bring Fido to play at the off-leash Motley Pet Stop & Rec Area in Motley, MN. This dog park is located behind the BP station on Main Street and offers a fenced area for your pup to enjoy. There are no trees providing shade, so be sure to bring along plenty of water for Fido.', 46.3373585, -94.6452968, '1st Ave S, Motley, MN, 56466', ARRAY['fenced', 'shade_trees', 'water_access']::text[], true, true, true),
  ('Off-Leash Dog Park at Roosevelt Park', 'Fido is invited to play at the Off-Leash Dog Park within Roosevelt Park in Richfield, MN. This fully fenced-in facility offers separate spaces for small and large breeds. Amenities include grooming stations, accessible picnic tables, lights and a fun pet photo booth.', 44.864331, -93.2706222, '7644 4th Ave S, Richfield, MN, 55423', ARRAY['fenced', 'lighting', 'picnic_tables']::text[], true, false, false),
  ('River Oaks Dog Park', 'River Oaks Dog Park is a recreational area in Moorhead, MN. The park features two separate areas for small and large dogs and dog agility equipment. There''s also a bench and a picnic table. Dog owners are reminded to bring water for their pets. There are walking paths surrounding the park.', 46.8331284, -96.7914695, '100 River Oaks Pt, Moorhead, MN, 56560', ARRAY['agility_equipment', 'benches', 'picnic_tables', 'separate_areas', 'walking_trails', 'water_access']::text[], false, true, false),
  ('Clearwater / Pleasant Regional Park Off-Leash Pet Area', 'Bring Fido to get some exercise at the Clearwater / Pleasant Regional Park Off-Leash Pet Area in Annandale, MN. This pup recreation space features a short trail and an unfenced open grassy area. Please note the pet-friendly area is not gated, so it is reserved for well-behaved pups with good recall. The space can be accessed by parking in the Disc Golf parking lot.', 45.2832393, -94.1149716, '10909 90th St NW, Annandale, MN, 55302', ARRAY['fenced', 'parking', 'water_access']::text[], true, true, false),
  ('Thompson Dog Park', 'Thompson Dog Park in Saint Peter, MN, features two separate areas for large and small dogs to run and play off leash. The park is fully fenced with a double-gated entrance. There are benches, picnic tables, a watering station for pets and a waste removal station.', 44.3389497, -93.9618699, '1125 N Swift St, Saint Peter, MN, 56082', ARRAY['benches', 'double_gated', 'fenced', 'picnic_tables', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Saint Bark', 'Saint Bark in St. Paul, MN, is an indoor and outdoor dog park where pups can play off-leash while owners relax with local brews, ciders, and seltzers. Enjoy supervised play, big-screen TVs, food trucks and complimentary Wi-Fi in a community-focused space. Entry is per dog; max two dogs per person. Guests must be 18+ for safety.', 44.9756751, -93.1681884, '1145 Snelling Ave N, St. Paul, MN, 55108', ARRAY[]::text[], false, false, false),
  ('Dog Park at Staring Lake Park', 'Visit the dog park at Staring Lake Park, a 5.7-acre off-leash area in Eden Prairie, MN. The park features include grassy open areas, natural walking paths and lovely wooded trails. This park closes after the first sufficient snow fall of the year to utilize the area as a Nordic ski trail, and is only open mid-March to December.', 44.8291461, -93.4477632, '13800 Pioneer Trl, Eden Prairie, MN, 55347', ARRAY['shade_trees', 'walking_trails', 'water_access']::text[], false, true, true),
  ('Fish Lake Regional Park Dog Off-Leash Area', 'Dogs can run and play in the seven-acre haven at the Fish Lake Regional Park Dog Off-Leash Area in Maple Grove, MN, which features a walking area as well as a smaller running area for small breeds.', 45.080896, -93.465482, '14900 Bass Lake Rd, Maple Grove, MN, 55311', ARRAY['water_access']::text[], false, true, false),
  ('Jean & Carl Frank Canine Park', 'The Jean & Carl Frank Canine Park in Rochester, MN is a large open space for dogs to play off leash. The park is equipped with a variety of agility equipment, including tunnels, bridges and more. Humans can watch from benches provided under the shade of trees. Be careful as sections of the park are not fully fenced. These sections lead to paved walking trails that you can also explore with your pooch. Dog owners are reminded to clean up any mess left behind.', 43.990672, -92.4420197, '1839 Pinewood Rd SE, Rochester, MN, 55904', ARRAY['agility_equipment', 'benches', 'fenced', 'shade_trees', 'walking_trails']::text[], true, false, true),
  ('Jean Duluth Dog Park', 'Jean Duluth Dog Park is a 3.5-acre fenced park at the northern edge of Duluth, MN. It has a double-gated entry and a separate area for dogs up to 20 pounds.', 46.872864, -92.0493854, '3567 Riley Rd, Duluth, MN, 55803', ARRAY['double_gated', 'fenced', 'separate_areas']::text[], true, false, false),
  ('Hibbing Dog Park', 'Pets will love to play off leash at the Hibbing Dog Park in Hibbing, MN. This fully fenced dog park includes a separate area for smaller pets.', 47.4322635, -92.9434522, '666-698 Greyhound Blvd, Hibbing, MN, 55746', ARRAY['fenced', 'separate_areas']::text[], true, false, false),
  ('Dog Park at Homeward Hills Park', 'Pets are welcome to run and play off leash at the dog park at Homeward Hills Park in Eden Prairie, MN. This fenced recreation area is open seasonally, from April-October, as it is used as a hockey rink during the winter months.', 44.8205287, -93.4299615, '12000 Silverwood Dr, Eden Prairie, MN, 55347', ARRAY['fenced']::text[], true, false, false),
  ('Minnehaha Off-Leash Dog Park', 'The Minnehaha Off-Leash Dog Park in Minneapolis, MN spans more than six acres and features a doggy beach to let your pup roam, socialize, splash and play. An off-leash permit is required to gain admittance. Pups must be leashed at all times when not in the designated off-leash area. Please note that the park is partially fenced.', 44.9060085, -93.2009502, '5399 Minnehaha Park Dr S, Minneapolis, MN, 55417', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Fairmont Dog Park', 'Bring Fido to blow off some steam at the Fairmont Dog Park, located adjacent to the walk/bike/run trail system and the Aquatic Park in Fairmount, MN. This 1.5-acre off-leash pup recreation area has a double-gated entryway with separate areas for small and large dogs. Dog owners are reminded to clean up any less that is left behind.', 43.6348892, -94.4565587, '1450 S Prairie Ave, Fairmont, MN, 56031', ARRAY['double_gated', 'separate_areas']::text[], false, false, false),
  ('Gary Mattson Dog Park', 'Gary Mattson Dog Park in Buffalo, MN, features a large off-leash fenced area where Fido can romp and play. There is a covered shelter with a picnic table where pet parents can rest. Leashed dogs can cool off in the water at Buffalo Lake, which is mere steps from the dog park.', 45.1739918, -93.9060801, '633 County Rd 35 W, Buffalo, MN, 55313', ARRAY['fenced', 'picnic_tables', 'water_access']::text[], true, true, false),
  ('Hackensack Dog Park', 'Bring Fido to Hackensack Dog Park in Hackensack, MN. This fenced, off-leash dog park is located within Bonna Park, and provides pups a safe place to run and play. The dog park has a water source for dogs to stay hydrated as they romp with their friends. Please note that dogs must be leashed until they reach the dog park.', 46.9307243, -94.5145736, '420 Lake Ave, Hackensack, MN, 56452', ARRAY['fenced', 'water_access']::text[], true, true, false),
  ('Eveleth Dog Park', 'Fido can enjoy time off-leash at Eveleth Dog Park in Eveleth, MN. This fenced dog park features trees, shade, big rocks, benches and waste receptacles. Please note that a permit and fee are required to utilize this dog park.', 47.4730059, -92.5361148, '701-1099 Fayal Ave, Eveleth, MN, 55734', ARRAY['benches', 'fenced', 'shade_trees']::text[], true, false, true),
  ('Breckenridge Dog Park', 'Breckenridge Dog Park is a great place to let Fido play off leash. The park features a 6-foot fence with double gates and includes a small and large dog area, doggy waste station, seating, and a bike rack. Dog owners are reminded to clean up any mess that is left behind in the park. Breckenridge Dog Park is open every day of the week from dawn until dusk.', 46.268309, -96.5897988, '504 Beede Ave, Breckenridge, MN, 56520', ARRAY['waste_bags']::text[], false, false, false),
  ('Dog Park at Pomme De Terre', 'Fido is invited to play at the dog park at Pomme De Terre in Morris, MN. The off-leash facility features two separate sections for large and small dogs. Amenities include agility equipment, water buckets and tennis balls, which visitors provide.', 45.5713117, -95.8821557, '230th St, Morris, MN, 56267', ARRAY['agility_equipment', 'separate_areas', 'water_access']::text[], false, true, false),
  ('Jackson Dog Park', 'Fido is invited to play at the Jackson Dog Park located in Jackson, MN. This off-leash area features two designated sections with a water fountain for you and your pup, as well as agility equipment. Shade and seating opportunities are available.', 43.6186426, -94.9867563, '101 1st St, Jackson, MN, 56143', ARRAY['agility_equipment', 'water_access']::text[], false, true, true),
  ('Carver Park Reserve Dog Off-Leash Area', 'Carver Park Reserve Dog Off-Leash Area is a 27-acre, completely fenced dog park with a separate area for small dogs. There is no water supply, so bring drinking water for your dog and yourself. A daily or annual pass is required. Don''t forget to clean up any mess that is left behind and to keep your pup under control at all times', 44.874198, -93.6563438, '7200 Park Dr, Victoria, MN, 55386', ARRAY['fenced', 'separate_areas', 'water_access']::text[], true, false, false),
  ('Savage Dog Park', 'Bring Fido to blow off some steam at the Savage Dog Park, which consists of more than two acres of fenced-off open space in Savage, MN. It includes separate areas for small and large dogs, four mailboxes that contain poop bags, running water during warm weather and dog toys.', 44.7779117, -93.3538559, '12425 Xenwood Ave, Savage, MN, 55378', ARRAY['fenced', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Bryant Lake Dog Off-Leash Area', 'Dogs go wild for the nine-acre, fully-fenced Bryant Lake Dog Off-Leash Area in Eden Prairie, MN. The site features a double-gated entry and includes an area for small dogs. Shelter and water are available. A daily or annual pass is required.', 44.8863933, -93.4341104, '6402 Rowland Rd, Eden Prairie, MN, 55344', ARRAY['double_gated', 'fenced', 'separate_areas', 'water_access']::text[], true, true, false),
  ('Arlington Arkwright Dog Park', 'Nestled in a residential neighborhood in St. Paul MN, the Arlington Arkwright Dog Park is a 4.5-acre park offering people and pets an opportunity to engage in social interaction and play. Dogs can be off leash in this fully-fenced area, and there are wooded trails and plenty of open space to run.', 44.9825415, -93.0835774, '400 Arlington Ave E, St. Paul, MN, 55130', ARRAY['fenced', 'shade_trees', 'walking_trails']::text[], true, false, true),
  ('Dog Park at Kiwanis Recreation Area', 'Visit the two large, fenced-in dog parks at the Kiwanis Recreation Area in Mankato, MN. Pups are free to play and run off leash at this park featuring tons of grass and trees. Poop bags are also provided.', 44.197591, -94.017257, '2301 US-169, Mankato, MN, 56001', ARRAY['fenced', 'shade_trees']::text[], true, false, true),
